import Tkinter as tk
import subprocess

# launcher config
width = 280
height = 300
meetIcon = ''
meetUrl = 'https://meet.google.com/'
chrome = "/Applications/Google Chrome.app/Contents/MacOS/Google Chrome"
pasteIcon = ''
clearIcon = ''

# meet chrome window config
chromeX = 0
chromeY = 0
chromeWidth = 300
chromeHeight = 800

window = tk.Tk()
window.title("Google Meet Launcher")
window.geometry('{}x{}+{}+{}'.format(width, height,
    (window.winfo_screenwidth()//2)-width//2, (window.winfo_screenheight()//2)-height//2))

mainIcon = tk.PhotoImage(data=meetIcon)
tk.Label(window, image=mainIcon).pack()

f = tk.Frame(window)
tk.Label(f, text="Meeting ID: ").grid(row=0,column=0)
idInput = tk.Entry(f, width=10)
idInput.grid(row=0,column=1)
idInput.focus()

def setText(text):
    # support pasting full meet urls
    text = text.replace(meetUrl, '')
    idInput.delete(0, tk.END)
    idInput.insert(0, text)

def getClipboard():
    return window.clipboard_get()

pasteButtonIcon = tk.PhotoImage(data=pasteIcon)
def pasteFromClipboard():
    setText(getClipboard())
tk.Button(f, image=pasteButtonIcon, width=27, height=20, command=pasteFromClipboard).grid(row=0,column=2)

clearButtonIcon = tk.PhotoImage(data=clearIcon)
def clearInput():
    setText('')
tk.Button(f, image=clearButtonIcon, width=27, height=19, command=clearInput).grid(row=0,column=3)

f.pack()

# opens sized & positioned chrome browser with meet url
def launch():
    script = '''
        window.moveTo({}, {});
        window.resizeTo({}, {});
        window.location="{}";
        '''.format(chromeX, chromeY, chromeWidth, chromeHeight, meetUrl + idInput.get())
    subprocess.Popen([chrome, '--app=data:text/html,<html><body><script>{}</script></body></html>'.format(script)])
    window.destroy()
tk.Button(window, text="Launch", command=launch).pack()

def keyLaunch(e):
    launch()
idInput.bind('<Return>', keyLaunch)

def updateId(e):
    text = idInput.get()
    if text.startswith(meetUrl):
        setText(text)
idInput.bind('<KeyRelease>', updateId)

# on startup, paste a meet id if the user already has one in the clipboard
if getClipboard().startswith(meetUrl):
    pasteFromClipboard()

window.lift() # bring app to front
window.mainloop()
